vals.css:
.w-elements-grid .w-elements-cell {
    flex-grow: 0;
    flex-shrink: 0;
    flex-basis: {{size_x_all}}px;
}


.owl-item>.w-elements-cell>.w-elements-cellwrapper>.cont>.node{
display: flex!important;
    flex-grow: 1!important;
    max-width: 100%!important;
    flex-direction: column!important;
}
.screen-xs .w-elements-grid .w-elements-cell {
    flex-basis: {{size_x_xs}}px;
    scroll-snap-align: start;
}
.screen-sm .w-elements-grid .w-elements-cell {
    flex-basis: {{size_x_sm}}px;
}
.screen-md .w-elements-grid .w-elements-cell {
    flex-basis: {{size_x_md}}px;
}

.w-elements-grid, .owl-stage {
    display: flex;
    flex-wrap: nowrap !important;
    justify-content: flex-start !important;
}
.screen-xs .w-elements-grid, .owl-stage {
    scroll-snap-type: x mandatory;
    scroll-snap-stop: always;
    width: 100%;
}

.w-elements-grid, .owl-stage {
margin: 0!important;
}
.w-elements-grid .w-elements-cell {
    flex-grow: 0;
    flex-shrink: 0;
    flex-basis: {{size_x_all}}px;
}
.screen-xs .w-elements-grid .w-elements-cell {
    flex-basis: {{size_x_xs}}px;

}
.screen-sm .w-elements-grid .w-elements-cell {
    flex-basis: {{size_x_sm}}px;
}
.screen-md .w-elements-grid .w-elements-cell {
    flex-basis: {{size_x_md}}px;
}

.w-elements-grid, .owl-stage {
    display: flex;
-webkit-overflow-scrolling: touch;
    flex-wrap: nowrap !important;
    overflow: hidden !important;
    justify-content: flex-start !important;
}
.w-elements-grid.owl-carousel{
overflow: visible !important;
}
.redactor .w-elements-grid, .owl-stage {
    overflow: auto !important;
}

.owl-dots {
  text-align: center;
  position: absolute;
  bottom: {{dotstop_owl_x_all}}%;
  left: {{dotsleft_owl_x_all}}%;
  transform: translateX(-{{dotsleft_owl_x_all}}%);
  z-index: 99!important;
  visibility: visible!important;
}
.owl-dots .owl-dot {
  width: {{dots_width_x_all}}px;
  height: {{dots_height_x_all}}px;
  border-radius: {{border_dots}}%;
  display: inline-block;
  background: {{color_dots.a_set(color_dots_op/100)}};
  margin: 0 3px;
}
<% if (dotsleft_owl_x_md !== null) { %>
    .screen-md .owl-dots{
        left: {{dotsleft_owl_x_md}}%;
    }
<% } %>
<% if (dotsleft_owl_x_sm !== null) { %>
    .screen-sm .owl-dots{
        left: {{dotsleft_owl_x_sm}}%;
    }
<% } %>
<% if (dotsleft_owl_x_xs !== null) { %>
    .screen-xs .owl-dots .owl-dot{
        left: {{dotsleft_owl_x_xs}}%;
    }
<% } %>

<% if (dotsleft_owl_x_md !== null) { %>
    .screen-md .owl-dots .owl-dot{
       transform: translateX(-{{dotsleft_owl_x_md}}%);
    }
<% } %>
<% if (dotsleft_owl_x_sm !== null) { %>
    .screen-sm .owl-dots .owl-dot{
        transform: translateX(-{{dotsleft_owl_x_sm}}%);
    }
<% } %>
<% if (dotsleft_owl_x_xs !== null) { %>
    .screen-xs .owl-dots .owl-dot{
       transform: translateX(-{{dotsleft_owl_x_xs}}%);
    }
<% } %>



<% if (dotstop_owl_x_md !== null) { %>
    .screen-md .owl-dots{
        bottom: {{dotstop_owl_x_md}}px;
    }
<% } %>
<% if (dotstop_owl_x_sm !== null) { %>
    .screen-sm .owl-dots{
        bottom: {{dotstop_owl_x_sm}}px;
    }
<% } %>
<% if (dotstop_owl_x_xs !== null) { %>
    .screen-xs .owl-dots{
        bottom: {{dotstop_owl_x_xs}}px;
    }
<% } %>
<% if (dots_height_x_md !== null) { %>
    .screen-md .owl-dots .owl-dot {
        height: {{dots_height_x_md}}px;
    }
<% } %>
<% if (dots_height_x_sm !== null) { %>
    .screen-sm .owl-dots .owl-dot {
        height: {{dots_height_x_sm}}px;
    }
<% } %>
<% if (dots_height_x_xs !== null) { %>
    .screen-xs .owl-dots .owl-dot {
        height: {{dots_height_x_xs}}px;
    }
<% } %>

<% if (dots_width_x_md !== null) { %>
    .screen-md .owl-dots .owl-dot {
        width: {{dots_width_x_md}}px;
    }
<% } %>
<% if (dots_width_x_sm !== null) { %>
    .screen-sm .owl-dots .owl-dot {
        width: {{dots_width_x_sm}}px;
    }
<% } %>
<% if (dots_width_x_xs !== null) { %>
    .screen-xs .owl-dots .owl-dot {
        width: {{dots_width_x_xs}}px;
    }
<% } %>

.owl-dots .owl-dot.active {
  background-color: {{color_dots_active.a_set(color_dots_op_active/100)}};
}
.owl-dots .owl-dot:focus {
  outline: none;

}

.owl-dots .owl-dot:hover {
  outline: none;
  background: {{color_dots_hover.a_set(color_dots_op_hover/100)}};
}
.owl-prev.disabled, .owl-next.disabled, .owl-next.disabled:after, .owl-prev.disabled:after{
opacity: 0;
cursor: auto;
}
.owl-prev, .owl-next  {
border: none;
position: absolute;
width: {{width_arr_x_all}}px;
height: {{height_arr_x_all}}px;
background: {{color_1.a_set(num_1/100)}};
top: {{arrtop_owl_x_all}}%;
transform: translateY(-{{arrtop_owl_x_all}}%);
transition: all 0.{{num_2}}s ease-in-out;
color: transparent;
border-radius: {{border_arr_bg}}px;
}

.owl-prev:after {
border: none;
 content: '';
 background: url('{{image_arr_left}}') left center no-repeat;
  background-size: contain;
  height: {{height_arr_x_all/2}}px;
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  width: {{width_arr_x_all/2}}px;
  z-index: 1000;
  cursor: pointer;
  color: transparent;
}

 .owl-next:after{
  border: none;
 content: '';
 background: url('{{image_arr_right}}') left center no-repeat;
  background-size: contain;
  height: {{height_arr_x_all/2}}px;
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  width: {{width_arr_x_all/2}}px;
  z-index: 1000;
  cursor: pointer;
  color: transparent;
 }
/*Положение левой стрелки*/
<% if (arrowl_left_x_md !== null) { %>
    .screen-md .owl-prev{
        left: {{arrowl_left_x_md}}%;
    }
<% } %>
<% if (arrowl_left_x_sm !== null) { %>
    .screen-sm .owl-prev{
        left: {{arrowl_left_x_sm}}%;
    }
<% } %>
<% if (arrowl_left_x_xs !== null) { %>
    .screen-xs .owl-prev{
        left: {{arrowl_left_x_xs}}%;
    }
<% } %>
/*Конец Положение левой стрелки*/
.owl-prev {
left: {{arrowl_left_x_all}}%;
}
.owl-next {
  right: {{arr_pos_x_all}}%;
}
/*Положение правой стрелки*/
<% if (arr_pos_x_md !== null) { %>
    .screen-md .owl-next{
        right: {{arr_pos_x_md}}%;
    }
<% } %>
<% if (arr_pos_x_sm !== null) { %>
    .screen-sm .owl-next{
        right: {{arr_pos_x_sm}}%;
    }
<% } %>
<% if (arr_pos_x_xs !== null) { %>
    .screen-xs .owl-next{
        right: {{arr_pos_x_xs}}%;
    }
<% } %>
/*Конец Положение правой стрелки*/
/*Ширина кнопок адаптив*/
<% if (width_arr_x_md !== null) { %>
    .screen-md  .owl-prev, .screen-md  .owl-next{
        width: {{width_arr_x_md}}px;
    }
   .screen-md .owl-prev:after, .owl-next:after {
       width: {{width_arr_x_md/2}}px;
   }
<% } %>
<% if (width_arr_x_sm !== null) { %>
    .screen-sm  .owl-prev, .screen-sm  .owl-next{
        width: {{width_arr_x_sm}}px;
    }
    .screen-sm .owl-prev:after, .owl-next:after {
       width: {{width_arr_x_sm/2}}px;
   }
<% } %>
<% if (width_arr_x_xs !== null) { %>
    .screen-xs  .owl-prev, .screen-xs  .owl-next{
        width: {{width_arr_x_xs}}px;
    }
   .screen-xs  .owl-prev:after, .owl-next:after {
       width: {{width_arr_x_xs/2}}px;
   }
<% } %>
/*Конец Ширина кнопок адаптив*/

/*Высота кнопок адаптив*/
<% if (height_arr_x_md !== null) { %>
    .screen-md .owl-prev, .screen-md .owl-next{
        height: {{height_arr_x_md}}px;
    }
   .screen-md  .owl-prev:after, .owl-next:after {
       height: {{width_arr_x_md/2}}px;
   }
<% } %>
<% if (height_arr_x_sm !== null) { %>
    .screen-sm  .owl-prev, .screen-sm .owl-next{
        height: {{height_arr_x_sm}}px;
    }
     .screen-sm  .owl-prev:after, .owl-next:after {
       height: {{width_arr_x_sm/2}}px;
   }
<% } %>
<% if (height_arr_x_xs !== null) { %>
    .screen-xs  .owl-prev, .screen-xs  .owl-next{
        height: {{height_arr_x_xs}}px;
    }
    .screen-xs .owl-prev:after, .owl-next:after {
       height: {{width_arr_xs/2}}px;
   }
<% } %>
/*конец Высота кнопок адаптив*/
/*Положение левой стрелки*/
<% if (arrtop_owl_x_md !== null) { %>
    .screen-md .owl-prev, .screen-md .owl-next{
        top: {{arrtop_owl_x_md}}%;
    }
<% } %>
<% if (arrtop_owl_x_sm !== null) { %>
    .screen-sm .owl-prev, .screen-sm .owl-next{
        top: {{arrtop_owl_x_sm}}%;
    }
<% } %>
<% if (arrtop_owl_x_xs !== null) { %>
    .screen-xs .owl-prev, .screen-xs .owl-next{
        top: {{arrtop_owl_x_xs}}%;
    }
<% } %>
/*Конец Положение левой стрелки*/
.owl-prev:hover.disabled, .owl-next:hover.disabled{
opacity: 0;
cursor: auto;
}
.owl-prev:hover,
.owl-next:hover {
  background: {{color_1_1.a_set(num_1_1/100)}};

}
.owl-theme .node.widget-elements  .w-elements-grid .w-elements-cell .w-elements-cellwrapper   {
 transition: all 0.3s ease-in-out;
 }

  .only-for-editor + .owl-theme .node.widget-elements  .w-elements-grid{
  overflow-x: scroll!important;
      flex-wrap: inherit;
      padding-top: 15px;
  }
